  Future<http.Response> propertytype() async {
    setState(() {
      loading = true;
    });

    var url = String_values.base_url + 'property-type';
    var response = await http.get(
      url,
      headers: {
        "Content-Type": "application/json",
        'Authorization': 'Bearer ${RegisterPagesState.token}'
      },
    );
    if (response.statusCode == 200) {
      setState(() {
        loading = false;
        li2 = VendorTariffPropertyDetailModelList.fromJson(
            jsonDecode(response.body));

        for (int i = 0; i < li2.tariff.length; i++)
          if (li2.tariff[i].ptypeId == li.res.planDatas.planPropertytypeId)
            setState(() {
              PropertyTypeController.text = li2.tariff[i].ptypeName;
            });

        // stateController.text=li2.items[int.parse(li.stateId)-1].stateName;
      });
      // for(int i=0;i<li.items.length;i++)
      //   stringlist.add(li.items[i].stateName);
      //dropdownValue1=stringlist[0];

    } else {
      setState(() {
        loading = false;
      });
      print("Retry");
    }
    print("response: ${response.statusCode}");
    print("response: ${response.body}");
    return response;
  }

